class Result < ActiveRecord::Base
  has_many :users, through: :questions
  belongs_to :question

  # extend Slugifiable::ClassMethods
  # include Slugifiable::InstanceMethods
  def self.find_by_description(search)
    bill = search.split(" ").join(", ")
    @foo.any? do |a, s|
      if s.match(/#{search}/)
        puts s
      end
    end
  end

